mapper.select:
    SELECT * FROM
	shisuan
	WHERE 1=1

        and bid=#{bid}


        and xiangmuname like concat('%',concat(#{xiangmuname}),'%')


        and  `time` BETWEEN #{kaishitime} AND #{time}

SELECT SUM(renshu) AS renshu,SUM(gongzi) AS gongzi,SUM(shichang) AS shichang,SUM(shuifei) AS shuifei,SUM(fangzu) AS fangzu,SUM(waibao) AS waibao,SUM(zhaodaifei) AS zhaodaifei,SUM(tongxunfei) AS tongxunfei,SUM(riyongpin) AS riyongpin, SUM(youjifei) AS youjifei,SUM(zuchefei) AS zuchefei,SUM(gaosutongxing) AS gaosutongxing,SUM(shineigongjiao) AS shineigongjiao, SUM(xiuchefei) AS xiuchefei,SUM(rengong) AS rengong,SUM(shuidian) AS shuidian,SUM(chepiao) AS chepiao,SUM(qita) AS qita,SUM(jieguo) AS jieguo, SUM(chuchaijiayou) AS chuchaijiayou, SUM(shebeixiuli) AS shebeixiuli,xiangmuname,`time` FROM shisuan WHERE 1=1
and bid=#{bid}
and xiangmuname like concat('%',concat(#{xiangmuname}),'%')
and `time` BETWEEN #{kaishitime} AND #{time}
GROUP BY xiangmuname
